}
	
class Beeroscope extends Component {
	constructor(props){
        super(props);
        this.state = {
          json: {},
		  beer: {}
        }
    }
	
	componentDidMount() {
        const URL = 'https://aztro.sameerkumar.website/?sign=aries&day=today';
		
        fetch(URL, {
            method: 'POST'
        }).then(response => response.json())
        .then(json => { this.setState({json}); });
	}
	
	componentDidUpdate() {

	}

    render() {
        return (
		<center>
		<h1>Click your sign to get your daily Beeroscope!</h1>
		<table id = "signs">
		<tr>
			<td><div class = "aries">
				<button onClick={getBeer}><img src="https://i.imgur.com/pa5dvx0.png" width="100" height="100"></img></button>
			</div></td>
			<td><div class = "taurus">
				<button onClick={getBeeroscope}><img src="https://i.imgur.com/WYv5Lry.png" width="100" height="100"></img></button>
			</div></td>
			<td><div class = "gemini">
				<button onClick="myFunction()"><img src="https://i.imgur.com/8Z59vyz.png" width="100" height="100"></img></button>
			</div></td>
			<td><div class = "cancer">
				<button onClick="myFunction()"><img src="https://i.imgur.com/qcHIjt8.png" width="100" height="100"></img></button>
			</div></td>
		</tr>
		
		<tr>
			<td><div class = "leo">
				<button onClick="myFunction()"><img src="https://i.imgur.com/kJ8UqgI.png" width="100" height="100"></img></button>
			</div></td>
			<td><div class = "virgo">
				<button onClick="myFunction()"><img src="https://i.imgur.com/JVxM4n2.png" width="100" height="100"></img></button>
			</div></td>
			<td><div class = "libra">
				<button onClick="myFunction()"><img src="https://i.imgur.com/m0iPdYX.png" width="100" height="100"></img></button>
			</div></td>
			<td><div class = "scorpio">
				<button onClick="myFunction()"><img src="https://i.imgur.com/EMWFhol.png" width="100" height="100"></img></button>
			</div></td>
		</tr>
		
		<tr>
			<td><div class = "sagittarius">
				<button onClick="myFunction()"><img src="https://i.imgur.com/dpfXFkU.png" width="100" height="100"></img></button>
			</div></td>
			<td><div class = "capricorn">
				<button onClick="myFunction()"><img src="https://i.imgur.com/sbmrFRE.png" width="100" height="100"></img></button>
			</div></td>
			<td><div class = "aquarius">
				<button onClick="myFunction()"><img src="https://i.imgur.com/EFICzQR.png" width="100" height="100"></img></button>
			</div></td>
			<td><div class = "pisces">
				<button onClick="myFunction()"><img src="https://i.imgur.com/bGqurNc.png" width="100" height="100"></img></button>
			</div></td>
		</tr></table>
		    <div id = "info">
				<br /><p>
			Sign ({this.state.json.date_range}) horoscope for {this.state.json.current_date} <br />
              <br />
              Description: {this.state.json.description} <br />
			  <br />
			</p>
			</div>
			<div id = "beer info"><p>
			  A Beer Recommendation: {this.state.beer.name} <br />
				{this.state.beer.description}
			</p></div>
		
		</center>
        );
    }
}

export default Beeroscope;
